import React, { useState } from 'react';
import { Analytics as AnalyticsType, Task } from '../types';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, PieChart, Pie, Cell, ResponsiveContainer } from 'recharts';
import { adminAPI } from '../services/api';
import '../styles/Dashboard.css';
import '../styles/Analytics.css';

interface AnalyticsProps {
  data: AnalyticsType;
}

interface TooltipState {
  category: string;
  tasks: Task[];
  position: { x: number; y: number };
}

const Analytics: React.FC<AnalyticsProps> = ({ data }) => {
  const [tooltip, setTooltip] = useState<TooltipState | null>(null);
  const [loading, setLoading] = useState(false);

  const statusData = [
    { name: 'To Do', value: data.todo_tasks, color: '#3498db', category: 'todo' },
    { name: 'In Progress', value: data.in_progress_tasks, color: '#f39c12', category: 'in_progress' },
    { name: 'Completed', value: data.completed_tasks, color: '#27ae60', category: 'completed' },
  ];

  const priorityData = [
    { name: 'High', value: data.high_priority_tasks, color: '#e74c3c', category: 'high_priority' },
    { name: 'Medium', value: data.medium_priority_tasks, color: '#f39c12', category: 'medium_priority' },
    { name: 'Low', value: data.low_priority_tasks, color: '#3498db', category: 'low_priority' },
  ];

  const renderCustomLabel = (entry: any) => {
    return `${entry.name}: ${entry.value}`;
  };

  const handleCardHover = async (category: string, event: React.MouseEvent) => {
    if (loading) return;

    setLoading(true);
    try {
      const response = await adminAPI.getTasksByCategory(category);
      const rect = event.currentTarget.getBoundingClientRect();
      
      setTooltip({
        category,
        tasks: response.data,
        position: {
          x: rect.left + rect.width / 2,
          y: rect.bottom + 10
        }
      });
    } catch (error) {
      console.error('Failed to load tasks:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCardLeave = () => {
    setTooltip(null);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric'
    });
  };

  const getCategoryTitle = (category: string) => {
    const titles: { [key: string]: string } = {
      total: 'All Tasks',
      completed: 'Completed Tasks',
      in_progress: 'In Progress Tasks',
      todo: 'To Do Tasks',
      overdue: 'Overdue Tasks',
      high_priority: 'High Priority Tasks',
      medium_priority: 'Medium Priority Tasks',
      low_priority: 'Low Priority Tasks',
    };
    return titles[category] || 'Tasks';
  };

  return (
    <div className="analytics-container">
      <h2>üìä Task Analytics Dashboard</h2>

      <div className="stats-grid">
        <div 
          className="stat-card hoverable"
          onMouseEnter={(e) => handleCardHover('total', e)}
          onMouseLeave={handleCardLeave}
        >
          <div className="stat-icon">üìä</div>
          <div className="stat-value">{data.total_tasks}</div>
          <div className="stat-label">Total Tasks</div>
          <div className="hover-indicator">Hover for details</div>
        </div>

        <div 
          className="stat-card success hoverable"
          onMouseEnter={(e) => handleCardHover('completed', e)}
          onMouseLeave={handleCardLeave}
        >
          <div className="stat-icon">‚úÖ</div>
          <div className="stat-value">{data.completed_tasks}</div>
          <div className="stat-label">Completed</div>
          <div className="hover-indicator">Hover for details</div>
        </div>

        <div 
          className="stat-card warning hoverable"
          onMouseEnter={(e) => handleCardHover('in_progress', e)}
          onMouseLeave={handleCardLeave}
        >
          <div className="stat-icon">‚è≥</div>
          <div className="stat-value">{data.in_progress_tasks}</div>
          <div className="stat-label">In Progress</div>
          <div className="hover-indicator">Hover for details</div>
        </div>

        <div 
          className="stat-card danger hoverable"
          onMouseEnter={(e) => handleCardHover('overdue', e)}
          onMouseLeave={handleCardLeave}
        >
          <div className="stat-icon">‚ö†Ô∏è</div>
          <div className="stat-value">{data.overdue_tasks}</div>
          <div className="stat-label">Overdue</div>
          <div className="hover-indicator">Hover for details</div>
        </div>
      </div>

      {/* Task Details Tooltip */}
      {tooltip && (
        <div 
          className="task-tooltip"
          style={{
            left: `${tooltip.position.x}px`,
            top: `${tooltip.position.y}px`,
          }}
        >
          <div className="tooltip-header">
            <h4>{getCategoryTitle(tooltip.category)}</h4>
            <span className="tooltip-count">{tooltip.tasks.length} task(s)</span>
          </div>
          
          <div className="tooltip-content">
            {tooltip.tasks.length === 0 ? (
              <p className="no-tasks">No tasks in this category</p>
            ) : (
              <ul className="task-list">
                {tooltip.tasks.map((task) => (
                  <li key={task.id} className={`task-item ${task.is_overdue ? 'overdue' : ''}`}>
                    <div className="task-item-header">
                      <span className="task-title">{task.title}</span>
                      <span className={`priority-badge priority-${task.priority}`}>
                        {task.priority}
                      </span>
                    </div>
                    <div className="task-item-meta">
                      <span>üìÖ {formatDate(task.due_date)}</span>
                      {task.assigned_to_name && (
                        <span>üë§ {task.assigned_to_name}</span>
                      )}
                    </div>
                    {task.is_overdue && task.status !== 'done' && (
                      <div className="overdue-indicator">‚ö†Ô∏è Overdue</div>
                    )}
                  </li>
                ))}
              </ul>
            )}
            
            {tooltip.tasks.length === 10 && (
              <p className="more-tasks">Showing first 10 tasks only</p>
            )}
          </div>
        </div>
      )}

      <div className="charts-grid">
        <div className="chart-container">
          <h3>Task Status Distribution</h3>
          <ResponsiveContainer width="100%" height={300}>
            <PieChart>
              <Pie
                data={statusData}
                cx="50%"
                cy="50%"
                labelLine={false}
                label={renderCustomLabel}
                outerRadius={100}
                fill="#8884d8"
                dataKey="value"
              >
                {statusData.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={entry.color} />
                ))}
              </Pie>
              <Tooltip />
            </PieChart>
          </ResponsiveContainer>
        </div>

        <div className="chart-container">
          <h3>Priority Distribution (Pending Tasks)</h3>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={priorityData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Bar dataKey="value" fill="#8884d8">
                {priorityData.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={entry.color} />
                ))}
              </Bar>
            </BarChart>
          </ResponsiveContainer>
        </div>
      </div>

      <div className="completion-rate">
        <h3>Completion Rate</h3>
        <div className="progress-bar">
          <div 
            className="progress-fill" 
            style={{ 
              width: `${data.total_tasks > 0 ? (data.completed_tasks / data.total_tasks) * 100 : 0}%` 
            }}
          >
            {data.total_tasks > 0 ? Math.round((data.completed_tasks / data.total_tasks) * 100) : 0}%
          </div>
        </div>
      </div>
    </div>
  );
};

export default Analytics;
